const openAIRequest = async (userQuery) => {

    const apiKey = process.env.REACT_APP_OPEN_AI_API_KEY;
    const apiUrl = 'https://api.openai.com/v1/chat/completions';

    const requestData = {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${apiKey}`
        },
        body: JSON.stringify({
            model: 'gpt-3.5-turbo',
            messages: [{ role: 'user', content: userQuery }],
            temperature: 0.7
        })
    };

    // {
    //     "id": "chatcmpl-7luQmJTWvS6Xl1t0nJM9TyxrtqNNM",
    //     "object": "chat.completion",
    //     "created": 1691652692,
    //     "model": "gpt-3.5-turbo-0613",
    //     "choices": [
    //       {
    //         "index": 0,
    //         "message": {
    //           "role": "assistant",
    //           "content": "The capital of Australia is Canberra."
    //         },
    //         "finish_reason": "stop"
    //       }
    //     ],
    //     "usage": {
    //       "prompt_tokens": 14,
    //       "completion_tokens": 7,
    //       "total_tokens": 21
    //     }
    //   }

    const response = await fetch(apiUrl, requestData)
    if (response.status === 200) {
        const body = await response.json()
        const aiMessage = await body.choices.
        console.log(aiMessage)
    }
    else if (response.status === 429) {
        // Rate limit exceeded 3 per min.
        return 'Please try after sometime.';
    }
    else {
        return 'An error has occurred, please refresh the page and try again.'
    }

}

export default openAIRequest
