const openAIRequest = (userQuery, apiKey) => {


    const apiUrl = 'https://api.openai.com/v1/chat/completions';

    console.log(apiKey)
    const requestData = {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${apiKey}`
        },
        body: JSON.stringify({
            model: 'gpt-3.5-turbo',
            messages: [{ role: 'user', content: userQuery }],
            temperature: 0.7
        })
    };

    fetch(apiUrl, requestData)
        .then(response => response.json()) // Get body from response in JSON format
        .then(data => {
            // Handle the response data here    
            console.log(data);
        })
        .catch(error => {
            // Handle errors here
            console.error('Error:', error);
        });

}

export default openAIRequest
